import styles from "./PlantPage1.module.css";
import PlantImage from "../components/PlantImage";
import HumidiBar from "../components/HumiBar";
import { NotifiText } from "./NotifiPage.js";

function RefreshButton() {
  const refreshPage = () => {
    window.location.reload();
  };

  return (
    <button className={styles.refresh} onClick={refreshPage}>
      !!ÏäµÎèÑ ÌôïÏù∏ÌïòÍ∏∞!!
    </button>
  );
}

function NotifiHomeBox({ className, item }) {
  function calculateTime(created_at) {
    const now = new Date();
    const createdTime = new Date(created_at);
    const diffInSeconds = Math.floor((now - createdTime) / 1000);

    if (diffInSeconds < 0) {
      alert("ÏïåÎ¶º Ïò§Î•ò: ÎØ∏ÎûòÏùò ÏïåÎ¶ºÏûÖÎãàÎã§.");
      throw new Error("ÏïåÎ¶º Ïò§Î•ò: ÎØ∏ÎûòÏùò ÏïåÎ¶ºÏûÖÎãàÎã§.");
    } else if (diffInSeconds < 60) {
      return "ÏßÄÍ∏à";
    } else if (diffInSeconds < 3600) {
      return `${Math.floor(diffInSeconds / 60)}Î∂Ñ Ï†Ñ`;
    } else if (diffInSeconds < 86400) {
      return `${Math.floor(diffInSeconds / 3600)}ÏãúÍ∞Ñ Ï†Ñ`;
    } else if (diffInSeconds < 2592000) {
      return `${Math.floor(diffInSeconds / 86400)}Ïùº Ï†Ñ`;
    } else {
      return `${Math.floor(diffInSeconds / 2592000)}Îã¨ Ï†Ñ`;
    }
  }

  return (
    <div className={styles.container}>
      <div className={styles.time}>{calculateTime(item.created_at)}</div>
      <div className={className}>
        <NotifiText className={styles.notifitext} message={item.message} />
      </div>
    </div>
  );
}

function NotifiHomeList({ className }) {
  const [items, setItems] = useState([]); // Ï¥àÍ∏∞Í∞íÏùÑ Îπà Î∞∞Ïó¥Î°ú ÏÑ§Ï†ï

  useEffect(() => {
    fetch("data/notifi.json")
      .then((response) => response.json())
      .then((data) => setItems(data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div className={className}>
      {items
        .slice(-3)
        .reverse()
        .map((item) => {
          return <NotifiHomeBox className={styles.notifihomebox} item={item} />;
        })}
    </div>
  );
}

