import React from "react";
import TopNav from "../components/TopNav";
import no_icon from "../assets/non_icon.png";
import MainNav from "../components/MainNav";
import "./SettingPage.css";
import PersonalsetBtn from "../components/PersonalsetBtn";
import { useNavigate } from "react-router-dom";

const notifibtn = {
  fontSize: "0.8em",
  textAlign: "center",
  borderRadius: "50px",
  border: "4px double #14c145",
  boxShadow: "0.1px 5px 12px 0.5px rgb(174, 174, 174)",
  textDecoration: "none" /* 밑줄 제거 */,
  alignSelf: "center",
  backgroundColor: "white",
  color: "#14c145",
  marginLeft: "50vw",
};

function SecurityPage(props) {
  const navigate = useNavigate();

  const logout = () => {
    // 로컬스트리지에서 토큰 삭제
    const data = JSON.parse(localStorage.getItem("data"));
    delete data.token;
    localStorage.setItem("data", JSON.stringify(data));
    alert("성공적으로 로그아웃 되었습니다.");
    navigate("/");
  };

  const deleteaccount = () => {
    const data = JSON.parse(localStorage.getItem("data"));
    const datatoken = data.token.access;

    fetch(" https://afed-163-152-3-182.ngrok-free.app/api/account/delete/", {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${datatoken} `,
      },

      mode: "cors",
    })
      .then((response) => {
        console.log(response);
        //요청이 성공적으로 이루어졌다면,
        if (response.ok) {
          localStorage.removeItem("data");
          alert("계정이 성공적으로 삭제되었습니다.");
          navigate("/");
        } else {
          throw new Error("계정을 삭제하는데 실패했습니다.");
        }
      })
      .catch((error) => {
        console.error(error);
        alert("계정을 삭제하는데 실패");
      });
  };

  return (
    <div className="setting">
      <TopNav
        className="topNav"
        text="개인/보안"
        link1={undefined}
        link2="/setting"
        icon1={no_icon}
        icon2={no_icon}
      />
      <br />
      <br />
      <div className="contents"></div>
      <PersonalsetBtn text="로그아웃" onClick={logout} />
      <PersonalsetBtn text="탈퇴하기" onClick={deleteaccount} />
      <MainNav />
    </div>
  );
}

export default SecurityPage;
