import React from "react";
import TopNav from "../components/TopNav";
import no_icon from "../assets/non_icon.png";
import setting_icon from "../assets/Setting_Icon.png";
import MainNav from "../components/MainNav";
import AccountBox from "../components/AccountBox";
import Profileimg1 from "../assets/on_Bell_Icon.png";
import Profileimg2 from "../assets/Setting_Icon.png";
import Profileimg3 from "../assets/Gallery_Icon.png";
import Profileimg4 from "../assets/Home_Icon.png";
import { useState } from "react";
import styles from "./GroupPage1.module.css";
import Btn3 from "../components/Btn3";

function GroupPage1() {
  const [isInvited, setisInvited] = useState(false);
  // const [familyMembers, setFamilyMembers] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const familyMembers = {
    familyMembers: [
      {
        nickname: "youngest",
        profile_picture:
          "https://search.pstatic.net/sunny/?src=https%3A%2F%2Fi.pinimg.com%2F736x%2F47%2F63%2Fd1%2F4763d159c22b4256cfbb9c284613008f.jpg&type=sc960_832",
        korean_name: "Park Kyeong-bin",
        is_current_user: true,
      },
      {
        nickname: "youngest",
        profile_picture:
          "https://search.pstatic.net/sunny/?src=https%3A%2F%2Fi.pinimg.com%2F736x%2F47%2F63%2Fd1%2F4763d159c22b4256cfbb9c284613008f.jpg&type=sc960_832",
        korean_name: "Lee Bo-reum",
        is_current_user: true,
      },
      {
        nickname: "youngest",
        profile_picture:
          "https://search.pstatic.net/sunny/?src=https%3A%2F%2Fi.pinimg.com%2F736x%2F47%2F63%2Fd1%2F4763d159c22b4256cfbb9c284613008f.jpg&type=sc960_832",
        korean_name: "Iboreumboreum",
        is_current_user: true,
      },
      {
        nickname: "Happy Cat",
        profile_picture:
          "https://search.pstatic.net/sunny/?src=https%3A%2F%2Fi.pinimg.com%2F736x%2F47%2F63%2Fd1%2F4763d159c22b4256cfbb9c284613008f.jpg&type=sc960_832",
        korean_name: "Iboreumboreum",
        is_current_user: true,
      },
    ],
  };

  // const handleInviteFamily = () => {
  //   setisInvited(!isInvited);
  // };

  // useEffect(() => {
  //   const accessToken = "your_access_token_here"; // 실제 액세스 토큰으로 대체해야 합니다.

  //   fetch("http://127.0.0.1:8000/api/account/", {
  //     method: "GET",
  //     headers: {
  //       Authorization: `Bearer ${accessToken}`,
  //     },
  //   })
  //     .then((response) => response.json())
  //     .then((data) => {
  //       setFamilyMembers(data.family_members);
  //       setIsLoading(true);
  //     })
  //     .catch((error) => {
  //       console.error("Error:", error);
  //     });
  // }, []);

  return (
    <div>
      <TopNav
        text="우리 가족"
        link1="undefined"
        link2="/setting"
        icon1={no_icon}
        icon2={setting_icon}
      />
      <div>
        <AccountBox
          className={styles.accountbox}
          src={Profileimg1}
          peoplename="이윤금"
          nickname="이 집 최고 권력자"
        />
        <Btn3 text="내 프로필 수정" link="/group2" />
        <div className={styles.graybox}></div>
        <h4 style={{ fontSize: "2.7em", marginLeft: "6vw", color: "#4D5053" }}>
          👨‍👩‍👧‍👦 연결된 가족
        </h4>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          familyMembers.map((member) => (
            <AccountBox
              className={styles.accountbox}
              src={member.profile_picture}
              peoplename={member.korean_name}
              nickname={member.nickname}
              key={member.korean_name} // 키로 한국 이름을 사용합니다. 이 값이 고유한지 확인하세요.
            />
          ))
        )}
      </div>
      <div className={styles.graybox}></div>
      <button
        // onClick={handleInviteFamily}
        style={{
          padding: "15px 30px",
          fontSize: "2.6em",
          fontWeight: "bold",
          fontSize: "2.7em",
          marginLeft: "3vw",
          marginTop: "3vw",
          backgroundColor: "transparent", // 또는 backgroundColor: '',
          color: "#4D5053", // 원하는 글자색을 지정하세요
          cursor: "pointer",
          border: "none", // 테두리 없음
        }}
      >
        {" "}
        ⨁ 가족 초대하기
      </button>
      {isInvited && <p>화분번호</p>}
      <MainNav />
    </div>
  );
}

export default GroupPage1;
