import React, { useEffect, useState } from "react";
import Btn2 from "../components/Btn2";
import TopNav from "../components/TopNav";
import on_bell_icon from "../assets/on_Bell_Icon.png";
import setting_icon from "../assets/Setting_Icon.png";
import MainNav from "../components/MainNav";
import "./Setting.css";
import PlantImage from "../components/PlantImage";
import HumidiBar from "../components/HumiBar";

function NotifiHome({ min = "3Î∂ÑÏ†Ñ", context = "ÏïåÎ¶º ÎÇ¥Ïö©" }) {
  return (
    <div className="NotifiHome">
      <div>{min}</div>
      <NotifiText context={context} />
    </div>
  );
}

export function NotifiText({ context = "ÏïåÎ¶º ÎÇ¥Ïö© Îì§Ïñ¥Í∞êÏöî" }) {
  return <div className="NotifiText">{context}</div>;
}

function PlantPage1({ userId }) {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    fetch(`https://your-api-url.com/users/${userId}`)
      .then((response) => response.json())
      .then((data) => setUserData(data))
      .catch((error) => console.error(error));
  }, [userId]);

  if (!userData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="Main">
      <TopNav
        className="topNav"
        text={undefined}
        link1="/notification"
        link2="/setting"
        icon1={on_bell_icon}
        icon2={setting_icon}
      />
      <h1>
        üå± {userData.korean_name}ÏôÄ Ìï®ÍªòÌïúÏßÄ {userData.start_date}Ïùº Ï∞®
      </h1>
      <NotifiHome />
      <NotifiHome />
      <div>
        <PlantImage />
        <p>{userData.nickname} Lv. 3</p>
      </div>
      <HumidiBar humidity={userData.moisture_level} />
      <Btn2 text="ÏÇ¨Ïö© Ï†ïÎ≥¥ ÏàòÏ†ï" link="/plant2" />
      <MainNav className="mainNav" />
    </div>
  );
}

export default PlantPage1;
