    start_date: "2023-01-01",
    plant_type: "유형1",
    moisture_level: 80,
  };
  const user = {
    id: 1,
    username: "user1",
    email: "user1@example.com",
    korean_name: "사용자1",
    profile_picture: 1,
    flower_pot: 1,
    notifications_enabled: true,
    nickname: "별명1",
